let number=document.querySelector(".dice-wrap__number"),button=document.querySelector(".dice-wrap__button");button.addEventListener("click",()=>{number.style.transform="scale(0)",setTimeout(()=>{number.textContent=diceRoll.d6(),number.style.transform="scale(1)"},1e3)});let diceRoll={randomInt:function(e,t){return Math.floor(Math.random()*(t-e+1))+e},d6:function(){return this.randomInt(1,20)}};